import React, { useEffect, useState } from 'react'
import "./style.css"
import pdeu from "./PDEU Logo.png";
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
import { saveAs } from 'file-saver';
import { useParams } from 'react-router-dom';
import axios from 'axios';

pdfMake.vfs = pdfFonts.pdfMake.vfs;


const CertificateOfRoomOne = () => {
    const id=useParams().idOfPerson1;
    const [person,setPerson]=useState({});

    const sendRequest = async()=>{
      const res = await axios.get(`https://dotslash-svnit-production.up.railway.app/api/v1/${id}`).catch((err)=>console.log(err));
      const data=await res.data;
      return data;
    }

    const generatePDF = () => {
        // Define the content and styles of the PDF document
        const documentDefinition = {
          content: [
            {
              stack: [
                {
                  image: "data:image/png;base64,", // Replace this with the URL or base64-encoded data of your image
                  width: 150,
                  height: 100,
                },
                {
                  text: 'Hostel Allotment Letter',
                  style: 'title',
                },
              ],
              style: 'header',
              margin: [0, 0, 0, 20],
            },
            {
              text: 'Certificate Content',
              style: 'subtitle',
              margin: [0, 0, 0, 10],
            },
            {
              ul: [
                {
                  text: `Name: ${person.name}`,
                },
                {
                  text: `Email-id: ${person.emailId}`,
                },
                {
                  text: `Phone No: ${person.phoneNumber}`,
                },
                {
                  text: `Room No: ${person.roomNo}`,
                },
              ],
              margin: [0, 0, 0, 20],
            },
            {
              text: 'Certificate Lower',
              style: 'subtitle',
              margin: [0, 0, 0, 10],
            },
            {
              text: 'The Document is Verfied and the person had been alloted the room',
            },
          ],
          styles: {
            header: {
              alignment: 'center',
            },
            title: {
              fontSize: 24,
              bold: true,
              alignment: 'center',
              margin: [0, 10, 0, 0],
            },
            subtitle: {
              fontSize: 16,
              bold: true,
              margin: [0, 0, 0, 10],
            },
          },
        };
      
        // Create the PDF document
        const pdfDocGenerator = pdfMake.createPdf(documentDefinition);
      
        // Download the PDF document
        pdfDocGenerator.getBlob((blob) => {
            try {
              saveAs(blob, 'certificate.pdf');
            } catch (error) {
              console.log(error);
            }
          });
    };
      
    useEffect(()=>{
      sendRequest().then((data)=>{
        setPerson(data);
        // console.log(person);
      })
    },[person]);
      

  return (
      <>
        <div id='certificate' className='certificate'>
            <div className='certificateTitle'>
                <img alt="" src="data:image/png;base64," />
                <h1>Hostel Allotment Letter</h1>
            </div>
            <div className='certificateContent'>
                <p><span className='title'>Name:</span> {person.name}</p>
                <p><span className='title'>College Email-id:</span> {person.emailId}</p>
                <p><span className='title'>Phone No:</span> {person.phoneNumber}</p>
                <p><span className='title'>Room No:</span> {person.roomNo}</p>
            </div>
            <div className='certificateLower'>
                <p>The Document is Verfied and the person had been alloted the room</p>
            </div>
        </div>
        <button className='downloadBtn' onClick={generatePDF}>Generate PDF</button>
      </>
  )
}

export default CertificateOfRoomOne